export const sizeConvert = (val)=> {
    let result = ""
    let megabyte = val / (1024 * 1024)
    if (megabyte >= 1) {
        result = megabyte.toFixed(2)
        return /^\d+\.00$/.test(result) ? megabyte.toFixed(0) + "M" : result + "M"
    }
    let kilobyte = val / 1024
    result = kilobyte.toFixed(2)
    return /^\d+\.00$/.test(result) ? kilobyte.toFixed(0) + "K" : result + "K"
}
export const trancateHead = (val) => {
    if (val.length <= 15) {
        return val
    }

    return "..." + val.substring(val.length - 15)

}
export const trancateTail = (val) => {
    if (val.length <= 15) {
        return val
    }

    return val.substr(0, 15) + "..."

}
export const percentage = (val)=> {
    val = val * 100
    return val.toFixed(2) + "%"
}