export const validateCompress = (compress)=> {
    if (compress.height && (isNaN(compress.height) || compress.height <= 0)) {
        console.warn("compress.height is invalid")
        return false
    }
    if (compress.width && (isNaN(compress.width) || compress.width <= 0)) {
        console.warn("compress.width is invalid")
        return false
    }

    if (compress.quality && (isNaN(compress.quality) || compress.quality <= 0 || compress.quality > 100)) {
        console.warn("compress.quality must between 0 and 100")
        return false
    }
    return true
}

export const getExtension = (fileName)=> {
    let index = fileName.lastIndexOf(".")
    if (index === -1) {
        return ""
    }
    return fileName.substring(index + 1)
}
